DROP TABLE IF EXISTS `bell_comment`;
CREATE TABLE `bell_comment` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `content` text DEFAULT NULL COMMENT '评论内容',
  `news_id` int(11) DEFAULT 0 COMMENT '新闻表id',
  `user_id` int(11) DEFAULT NULL COMMENT '用户表id',
  `create_time` int(11) DEFAULT 0 COMMENT '写入时间戳',
  `update_time` int(11) DEFAULT 0 COMMENT '修改时间戳',
  `is_show` tinyint(1) DEFAULT 0 COMMENT '显示标识，0不显示，1显示',
