  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=14 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of bell_comment
-- ----------------------------
BEGIN;
INSERT INTO `bell_comment` VALUES (1, '《旺达·幻视》改编自漫威漫画，由美国漫威影业公司出品，属于漫威电影宇宙', 14, 1, 1639708663, 0, 1, 0);
INSERT INTO `bell_comment` VALUES (2, '讲述旺达和幻视两位超能力者在理想的郊区生活，逐渐开始怀疑现实并不是所见的那样。', 14, 1, 1639708688, 0, 1, 0);
INSERT INTO `bell_comment` VALUES (3, '该剧设定在上世纪五十年代，紧接《复仇者联盟4》结尾上演。', 14, 3, 1639708718, 0, 1, 0);
INSERT INTO `bell_comment` VALUES (4, '故事围绕幻视和绯红女巫的爱情和冒险展开，主线时间发生在《复仇者联盟4：终局之战》之后。', 14, 2, 1639708770, 0, 1, 0);
INSERT INTO `bell_comment` VALUES (5, '自从幻视死后，旺达（伊丽莎白·奥尔森 饰）便失去理智的用魔法控制了小镇“西景镇”', 23, 2, 1639709017, 0, 1, 0);
INSERT INTO `bell_comment` VALUES (6, '她以情景剧为原型，创造了一个属于自己的世界，并创造了全新的幻视', 23, 2, 1639709035, 0, 1, 0);
INSERT INTO `bell_comment` VALUES (7, '而小镇居民们也被魔法同化成剧中人物。', 28, 1, 1639717050, 0, 0, 1);
INSERT INTO `bell_comment` VALUES (8, '在这个世界中，她跟幻视恩爱幸福，还有了双胞胎儿子汤米和比利。', 14, 1, 1639722049, 0, 1, 0);
INSERT INTO `bell_comment` VALUES (9, '但天剑局的介入打破了旺达的宁静，潜伏在小镇的女巫阿加莎·哈克尼斯', 3, 3, 1639725498, 0, 1, 0);
INSERT INTO `bell_comment` VALUES (10, '作为一个设计师,如果遭到质疑你是否能恪', 3, 3, 1639725508, 0, 1, 0);
INSERT INTO `bell_comment` VALUES (11, '《旺达·幻视》改编自漫威漫画，由美国漫威影业公司出品，属于漫威电影宇宙', 5, 3, 1639725527, 0, 1, 0);
INSERT INTO `bell_comment` VALUES (12, '测试间隔', 29, 5, 1639892764, 0, 1, 0);
INSERT INTO `bell_comment` VALUES (13, '123123', 29, 5, 1639892829, 0, 1, 0);
COMMIT;

