-- ----------------------------
-- Table structure for bell_news
-- ----------------------------
DROP TABLE IF EXISTS `bell_news`;
CREATE TABLE `bell_news` (
  `id` mediumint(8) NOT NULL AUTO_INCREMENT,
  `cid` smallint(3) DEFAULT 0 COMMENT '所在分类',
  `title` varchar(50) DEFAULT '' COMMENT '新闻标题',
  `pic` varchar(200) DEFAULT '' COMMENT '文章封面',
  `keywords` varchar(50) DEFAULT '' COMMENT '文章关键字',
  `description` varchar(255) DEFAULT '' COMMENT '文章描述',
  `status` tinyint(1) DEFAULT 0,
  `summary` varchar(255) DEFAULT '' COMMENT '文章摘要',
  `create_time` int(10) DEFAULT 0 COMMENT '创建时间',
  `update_time` int(10) DEFAULT 0 COMMENT '编辑时间',
  `content` text DEFAULT NULL COMMENT '文章内容',
  `views` int(10) DEFAULT 0 COMMENT '浏览量',
  `aid` smallint(3) DEFAULT 0 COMMENT '发布者UID',
  `is_del` tinyint(1) unsigned DEFAULT 0 COMMENT '删除标识',
  `is_show` tinyint(1) DEFAULT 1 COMMENT '显示标识，0不显示，1显示',
  `is_recommend` tinyint(1) unsigned DEFAULT 0 COMMENT '是否推荐，1：推荐',
  `is_top` tinyint(1) DEFAULT 0 COMMENT '是否置顶，1:置顶，0无',
  `read_level` int(3) DEFAULT 0 COMMENT '阅读',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=38 DEFAULT CHARSET=utf8 COMMENT='新闻表';

-- ----------------------------
-- Records of bell_news
-- ----------------------------
BEGIN;
INSERT INTO `bell_news` VALUES (1, 5, '作为一个设计师,如果遭到质疑你是否能恪守自己的原则?', '20211210/58b77875c10c3a04770de24101edd43f.jpg', NULL, NULL, NULL, '曾经有站长找我求助，他说他不知道该怎么办，自己做出来的网站，不仅没有得到大家的认可，反而让大家给他开了一个评判大会。他自己认为已经是做的最好的，却遭受大家无情的指责是“垃圾”作品。', 1639122971, 1639834085, '&lt;p style=&quot;box-sizing: border-box; outline: 0px; margin-top: 0px; margin-bottom: 16px; padding: 0px; color: rgb(77, 77, 77); overflow: auto hidden; overflow-wrap: break-word; font-family: -apple-system, &amp;quot;SF UI Text&amp;quot;, Arial, &amp;quot;PingFang SC&amp;quot;, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, &amp;quot;WenQuanYi Micro Hei&amp;quot;, sans-serif, SimHei, SimSun; white-space: normal; background-color: rgb(255, 255, 255); line-height: 26px !important;&quot;&gt;曾经有站长找我求助，他说他不知道该怎么办，自己做出来的网站，不仅没有得到大家的认可，反而让大家给他开了一个评判大会。他自己认为已经是做的最好的，却遭受大家无情的指责是“垃圾”作品。&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; outline: 0px; margin-top: 0px; margin-bottom: 16px; padding: 0px; color: rgb(77, 77, 77); overflow: auto hidden; overflow-wrap: break-word; font-family: -apple-system, &amp;quot;SF UI Text&amp;quot;, Arial, &amp;quot;PingFang SC&amp;quot;, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, &amp;quot;WenQuanYi Micro Hei&amp;quot;, sans-serif, SimHei, SimSun; white-space: normal; background-color: rgb(255, 255, 255); line-height: 26px !important;&quot;&gt;有的给他提出了意见，有的没有提意见，直接扔了两个字，“垃圾”。这没有谁对谁错，每个人的审美观点都不一样，我们能做的就是尊重别人。我相信他是认真对待网站的，我仔细看了他的设计，布局，颜色搭配都是他精心的设计。美和丑是没有一个评判标准的。我给他讲了一个关于建筑设计师的故事。&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; outline: 0px; margin-top: 0px; margin-bottom: 16px; padding: 0px; color: rgb(77, 77, 77); overflow: auto hidden; overflow-wrap: break-word; font-family: -apple-system, &amp;quot;SF UI Text&amp;quot;, Arial, &amp;quot;PingFang SC&amp;quot;, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, &amp;quot;WenQuanYi Micro Hei&amp;quot;, sans-serif, SimHei, SimSun; white-space: normal; background-color: rgb(255, 255, 255); line-height: 26px !important;&quot;&gt;300多年前，建筑设计师克里斯托·莱伊恩受命设计了英国温泽市政府大厅，他运用工程力学的知识，依据自己多年的实践经验，巧妙地设计了只用一根柱子支撑的大厅天花板。但是一年以后，在进行工程验收时，市政府权威人士却对此提出了质疑，并要求莱伊恩一定要再多加几根柱子。莱伊恩对自己的设计很自信，因此他非常苦恼：坚持自己的主张吧，他们肯定会另找人修改设计；不坚持吧，又有悖自己为人的准则。&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; outline: 0px; margin-top: 0px; margin-bottom: 16px; padding: 0px; color: rgb(77, 77, 77); overflow: auto hidden; overflow-wrap: break-word; font-family: -apple-system, &amp;quot;SF UI Text&amp;quot;, Arial, &amp;quot;PingFang SC&amp;quot;, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, &amp;quot;WenQuanYi Micro Hei&amp;quot;, sans-serif, SimHei, SimSun; white-space: normal; background-color: rgb(255, 255, 255); line-height: 26px !important;&quot;&gt;莱伊恩的故事，向我们讲述了一个深刻的哲理“恪守着自己的原则，哪怕遭遇到最大的阻力，也要想办法抵达胜利。”&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; outline: 0px; margin-top: 0px; margin-bottom: 16px; padding: 0px; color: rgb(77, 77, 77); overflow: auto hidden; overflow-wrap: break-word; font-family: -apple-system, &amp;quot;SF UI Text&amp;quot;, Arial, &amp;quot;PingFang SC&amp;quot;, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, &amp;quot;WenQuanYi Micro Hei&amp;quot;, sans-serif, SimHei, SimSun; white-space: normal; background-color: rgb(255, 255, 255); line-height: 26px !important;&quot;&gt;就拿我自己来说吧，有时候会很矛盾，设计好的作品，不把它分享出来，会觉得待在自己电脑里面实在是没有意义。干脆就发布出去吧。我也害怕收到大家不好的评论，有些评论，可能说者无意，但是对于每一个用心的站长来说，都会受很深的影响，愤怒，恼羞。&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; outline: 0px; margin-top: 0px; margin-bottom: 16px; padding: 0px; color: rgb(77, 77, 77); overflow: auto hidden; overflow-wrap: break-word; font-family: -apple-system, &amp;quot;SF UI Text&amp;quot;, Arial, &amp;quot;PingFang SC&amp;quot;, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, &amp;quot;WenQuanYi Micro Hei&amp;quot;, sans-serif, SimHei, SimSun; white-space: normal; background-color: rgb(255, 255, 255); line-height: 26px !important;&quot;&gt;心态很重要，再来看莱伊恩，他当时顶着多大的压力呀。同样作为一个前端设计师，哪怕遭到质疑，我们也要像莱伊恩一样恪守自己的原则。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 1, 2, 0, 1, 0, 0, 0);
INSERT INTO `bell_news` VALUES (2, 1, '个人网站做好了，百度不收录怎么办？来，', '20211210/7ddb419cc2ca5797abbc11db3e355b4f.jpg', NULL, NULL, NULL, '个人博客模板也停止于《草根寻梦》，这套模板清新，很多人喜欢，但不是响应式的，响应式对当时来说，虽然还比较新颖，但还不流行。除了在个人博客网站上放QQ群，我没有放任何联系方式，因为带孩子嘛，不能随时看手机，也不想被打扰。', 1639122527, NULL, '<p>从心出发</p><p>个人博客模板也停止于《草根寻梦》，这套模板清新，很多人喜欢，但不是响应式的，响应式对当时来说，虽然还比较新颖，但还不流行。除了在个人博客网站上放QQ群，我没有放任何联系方式，因为带孩子嘛，不能随时看手机，也不想被打扰。</p><p>个人博客模板也停止于《草根寻梦》，这套模板清新，很多人喜欢，但不是响应式的，响应式对当时来说，虽然还比较新颖，但还不流行。除了在个人博客网站上放QQ群，我没有放任何联系方式，因为带孩子嘛，不能随时看手机，也不想被打扰。</p>', 20, 2, 0, 1, 0, 0, 0);
