-- ----------------------------
-- Table structure for bell_news_category
-- ----------------------------
DROP TABLE IF EXISTS `bell_news_category`;
CREATE TABLE `bell_news_category` (
  `id` int(5) NOT NULL AUTO_INCREMENT,
  `pid` int(5) DEFAULT 0 COMMENT 'parentCategory上级分类',
  `cate_name` varchar(20) DEFAULT '' COMMENT '分类名称',
  `pic` varchar(255) DEFAULT '' COMMENT '图片路径',
  `introduce` varchar(255) DEFAULT '' COMMENT '介绍',
  `is_del` tinyint(1) DEFAULT 0 COMMENT '删除标识',
  `created_at` int(10) DEFAULT 0 COMMENT '添加时间',
  `updated_at` int(10) DEFAULT 0 COMMENT '修改时间',
  `level` tinyint(1) DEFAULT 0 COMMENT '级别',
  `sort` int(10) DEFAULT 0,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8 COMMENT='新闻分类表';

-- ----------------------------
-- Records of bell_news_category
-- ----------------------------
BEGIN;
INSERT INTO `bell_news_category` VALUES (1, 0, '个人博客', '', '个人博客日记，记录一些优秀个人站长是如何制作个人博客，建个人博客、以及经营个人网站的，本站还会推荐一些优秀的个人博客站长网站。', 0, 1639028893, 1639578795, 0, 0);
INSERT INTO `bell_news_category` VALUES (2, 0, '企业网站', '', '博客网站制作，本栏目主要是分享关于域名选择、空间购买，个人博客网站模板制作，网站后期维护运营，优化等相关文章，以及提供帝国cms建站过程中遇到问题的解决办法。', 0, 1639028922, 1639578826, 0, 0);
INSERT INTO `bell_news_category` VALUES (3, 0, '经典案例', '', '很多事情的解决需要借鉴案例，因为案例之所以能成为案例是案例的解决方法或者思路符合一般的问题解决，接下来让我们来看看案例分析的经典范文吧。', 0, 1639028937, 1639621097, 0, 0);
INSERT INTO `bell_news_category` VALUES (4, 0, '外贸网站44', '', '', 1, 1639028942, 1639028985, 0, 0);
INSERT INTO `bell_news_category` VALUES (5, 0, '生活杂记', '', '当你听见宝宝在产房里发出第一声啼哭时，那么恭喜你你已经成为了一个父亲。你在老婆心中完美丈夫的形象中又多了一份责任。在现在的社会中生活节奏很快，很多的妈妈既要工作又要上班。所以，你就要成为一名合格的奶爸。继而你在老婆心中完美形象，便牢不可破。', 0, 1639028951, 1639828587, 0, 0);
INSERT INTO `bell_news_category` VALUES (6, 0, '33333', '', '', 1, 1639029057, 1639029405, 0, 333);
INSERT INTO `bell_news_category` VALUES (7, 0, '123123', '', '', 1, 1639029412, NULL, 0, 123);
COMMIT;

