-- ----------------------------
-- Table structure for bell_user
-- ----------------------------
DROP TABLE IF EXISTS `bell_user`;
CREATE TABLE `bell_user` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user_name` varchar(50) DEFAULT NULL COMMENT '用户名',
  `password` varchar(50) DEFAULT NULL COMMENT '密码',
  `nickname` varchar(50) DEFAULT '' COMMENT '昵称',
  `email` varchar(255) DEFAULT '' COMMENT '邮箱',
  `phone` varchar(15) DEFAULT NULL COMMENT '电话号',
  `mobile` varchar(15) DEFAULT NULL COMMENT '手机号',
  `gender` tinyint(1) DEFAULT 0 COMMENT '性别，1男，2女',
  `user_level` int(3) DEFAULT 1 COMMENT '用户登录，1注册用户，2普通会员，3高级会员，4vip会员',
  `is_del` tinyint(1) DEFAULT 0 COMMENT '删除标识，0正常，1删除',
  `created_at` int(11) DEFAULT NULL COMMENT '创建时间',
  `updated_at` int(11) DEFAULT NULL COMMENT '修改时间',
  `is_locked` tinyint(1) DEFAULT 0 COMMENT '锁定标识，0正常，1上锁',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=utf8;

